#include <bits/stdc++.h> using namespace std; typedef long long LL; typedef unsigned long long ULL; int main() {  ios::sync_with_stdio(false); cin.tie(0); cout.tie(0); int N; cin >> N; LL count = 0; for (int j = 0; j < N; j++) { count++; int n, x; cin >> n >> x; vector<int> a; vector<int> b; for (int i = 0; i < n; i++) { int tmp; cin >> tmp; a.push_back(tmp); } for (int i = 0; i < n; i++) { int tmp; cin >> tmp; b.push_back(tmp); } sort(a.begin(), a.end()); sort(b.begin(), b.end()); reverse(b.begin(), b.end()); bool ok = true; for (int i = 0; i < n; i++) { if (a[i] + b[i] > x) { cout << "Case " << j + 1 << ": No" << endl; ok = false; break; } } if (ok) { cout << "Case " << j + 1 << ": Yes" << endl; } } }